INSERT INTO `job` VALUES ('1645982636419579906', 1264, '1645339396007915521', 'java研发工程师', '[\"技术\",\"后端开发\",\"Java\"]', '本科', 6, 14, 14, '[\"福建省\",\"福州市\"]', '[\"119.337154\",\"26.056704\"]', '腾讯众创空间(福州)', NULL, '[\"后端工程师\",\"Java\",\"MySQL\",\"计算机/软件工程相关经验\",\"Java开发经验\",\"大数据处理经验\",\"Flink\",\"大数据\",\"Hadoop\"]', '[\"五险一金\",\"补充医疗保险\",\"定期体检\",\"加班补助\",\"年终奖\",\"带薪年假\",\"股票期权\",\"零食下午茶\",\"节日福利\"]', 3, 1, 0, '<p><span style=\"color: rgb(51, 51, 51); background-color: rgb(255, 255, 255); font-size: 15px;\">岗位职责：<br>负责CSIG安全合规数据中台的研发工作，分析系统相关需求、设计技术架构、落地技术方案；<br>挖掘系统性能、稳定性、可靠性等潜在风险并跟进优化，持续保障和提升产品质量；<br>结合业务场景提供必要的技术支持，助力数据中台能力在业务的落地。<br>注：此岗位为腾讯集团旗下子公司编制岗位<br>岗位要求：<br>计算机或软件相关专业，本科及以上学历，具备扎实的计算机和软件技术基础；<br>熟悉掌握java编程语言，有高并发场景下的系统设计经验；<br>熟练使用mysql/redis等数据库，有mysql性能调优经验；熟悉ELK、kafka、flink、doris等，有大数据平台开发经验者优先；<br>具有良好的代码风格、接口设计规范；<br>在性能优化、持续集成、静态分析、框架设计和分布式系统等领域中，有涉及到一项或多项相关的实践经验；<br>在项目管理、配置管理、CI/CD、devops、workflow等一到多个领域有实践及推动经验者优先；<br>热爱学习，具有自我持续优化的驱动力，熟悉docker、K8s、cloudnative、微服务并有实践者更佳；<br>熟悉云计算产品，有相关从业经验者优先；<br>具备良好的团队合作精神，善于协调沟通，具备较强的问题定位和推动解决能力。</span></p>', '2023-04-12 10:50:16', '2023-04-26 14:44:06');
INSERT INTO `job` VALUES ('1646014724975886338', 1265, '1645339616334704642', '网龙招聘电工', '[\"生产制造\",\"技工/普工\",\"电工\"]', '学历不限', 4, 7, 13, '[\"福建省\",\"福州市\"]', '[\"119.7001\",\"25.95985\"]', '网龙网络科技有限公司', NULL, '[\"电机修理电工\",\"电工操作证\"]', '[\"五险一金\",\"补充医疗保险\",\"免费班车\",\"节日福利\",\"加班补助\"]', 69, 1, 0, '<p><span style=\"color: rgb(51, 51, 51); background-color: rgb(255, 255, 255); font-size: 15px;\">福州地区面试的人员<br>方案①： 可在9：00时乘坐班车。<br>方案②：地铁路线 1号线→6号线（万寿站）→624/642（海西动漫城站）→步行800米（数字教育小镇）<br>一、工作内容<br>机电设备、消防泵房、电梯、泳池、办公区等区域设备使用情况<br>二、工作地点及时间<br>长乐-网龙网络公司<br>8：30-17：30 周末双休<br>四、岗位福利<br>1、**********；<br>2、每个月提供餐补，免费提供住宿（按级别提供集体宿舍、公寓式宿舍，独立卫生间、阳台、厨房，配空调、滚筒洗衣机、冰箱、50寸液晶电视、独立的wifi等电器设备）；<br>3、完善的员工生活设施（配有4个泳池，网球场、篮球场、沙滩排球场、足球场、壁球室、搏击台、员工电影院、瑜珈教室、健身房等）；<br>4、丰富的员工活动安排（生日会、节日晚会、系列文体比赛等）；<br>5、免费为员工提供入职体检、年度体检；<br>6、每天免费提供多趟次的班车往返于福州、长乐。<br>原标题：《物业电工（白班周末双休）》</span></p>', '2023-04-12 12:57:46', '2023-04-23 15:41:24');
